mapper.select:
select * from
schedule_sf where user_idx = #{user_idx}
order by SCHD_IDX desc

SELECT url FROM
update_image WHERE feed_idx =
#{feed_idx}

select COUNT(*)
from feed_heart
where feed_idx = #{feed_idx}

select COUNT(*)
from feed_heart
where user_idx = #{user_idx}
and feed_idx = #{feed_idx}

mapper.delete:
delete from
feed_heart where feed_idx = #{feed_idx}
and user_idx=#{user_idx}

mapper.select:
select count(*) from feed_subscribe
where feeder = #{feeder}
and subscriber = #{subscriber}

mapper.delete:
delete from feed_subscribe
where feeder = #{feeder}
and subscriber = #{subscriber}
