mapper.select:
select count(*)
from reply_like where liker =
#{liker}
and feed_idx = #{feed_idx} and
reply_idx = #{reply_idx}

mapper.delete:
delete from
reply_like where
liker = #{liker}
and
feed_idx = #{feed_idx}
and
reply_idx
= #{reply_idx}

mapper.select:
select count(*)
from reply_like where reply_idx =
#{reply_idx}

select count(*)
from reply_dislike where disliker =
#{disliker}
and feed_idx =
#{feed_idx} and reply_idx = #{reply_idx}

mapper.delete:
delete from
reply_dislike where
disliker = #{disliker}
and
feed_idx = #{feed_idx}
and
reply_idx = #{reply_idx}

mapper.select:
select count(*)
from reply_dislike where reply_idx =
#{reply_idx}
